        function getReport(key){
            $.ajax({
                url : "/report-actions",
                data : "key="+key,
                type : "post",
                dataType : "json",
                success : function(response){
                    if(response.success){
                        $.each( response.data, function( key, val ) {
                            $("#"+key).html(val);
                        });
                    }
                }
            });
        }
